pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

interface IMasterChefV1 {
    function withdraw(uint256 _pid, uint256 _amount) external;
    function deposit(uint256 _pid, uint256 _amount) external;
}

abstract contract BaseServer is Ownable {
  IMasterChefV1 public constant masterchefV1 = IMasterChefV1(0xc2EdaD668740f1aA35E4D8f227fB8E17dcA888Cd);
  IERC20 public constant sushi = IERC20(0x6B3595068778DD592e39A122f4f5a5cF09C90fE2);

  uint256 public immutable pid;

  address public immutable minichef;

  event Harvest(uint256 indexed pid);
  event Withdraw(uint256 indexed pid, uint256 indexed amount);
  event Deposit(uint256 indexed pid, uint256 indexed amount);

  constructor(uint256 _pid, address _minichef) {
    pid = _pid;
    minichef = _minichef;
  }

  function harvestAndBridge() public {
    masterchefV1.withdraw(pid, 0);
    bridge();
    emit Harvest(pid);
  }

  function withdraw() public onlyOwner {
    masterchefV1.withdraw(pid, 1);
    emit Withdraw(pid, 1);
  }

  function deposit(address token) public onlyOwner {
    IERC20(token).approve(address(masterchefV1), 1);
    masterchefV1.deposit(pid, 1);
    emit Deposit(pid, 1);
  }

  function bridge() public virtual;
}","keccak256":"0xae155f8dcbc0d3b5391e1454ef54caeaade8808b2f28af60517b3332f17f57be","license":"MIT"},"contracts/implementations/xDaiServer.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import '../BaseServer.sol';

interface IxDaiBridge {
  function relayTokens(address token, address _receiver, uint256 _value) external;
}

contract xDaiServer is BaseServer {
  address public constant bridgeAddr = 0x88ad09518695c6c3712AC10a214bE5109a655671;

  event BridgedSushi(address indexed minichef, uint256 indexed amount);

  constructor(uint256 _pid, address _minichef) BaseServer(_pid, _minichef) {}

  function bridge() public override {
    uint256 sushiBalance = sushi.balanceOf(address(this));

    sushi.approve(bridgeAddr, sushiBalance);
    IxDaiBridge(bridgeAddr).relayTokens(address(sushi), minichef, sushiBalance);
    emit BridgedSushi(minichef, sushiBalance);
  }
}","keccak256":"0xa354e22180ae1f07cdea218497ce1da8e8727c93940658b81fd0bb6e34d95e7a","license":"MIT"}},"version":1}
